import React, {useState} from 'react';
import './App.css';
import {Inject, ScheduleComponent, ViewsDirective, ViewDirective, Day, Week, Month, Agenda, Resize, DragAndDrop} from '@syncfusion/ej2-react-schedule';
import { ButtonComponent } from '@syncfusion/ej2-react-buttons';

function App() {
  const[events, setEvents] = useState([]);
  const[calDataSource, setCalDataSource] = useState([]);
  //const[scheduleObj, setScheduleObj] = useState({});

  var scheduleObj, currentEvent, currentSchedule;
  var domain = 'http://localhost:3000/calendar/events'

  const fetchEvents = () => {
    fetch("http://localhost:3000/calendar/events")
    .then(response => response.json())
    .then(res => {
      //alert("Results from server: " + JSON.stringify(res.events));
      if(res && res.events){
        setEvents(res.events);
      }
    });
  }

  const getEventObject = (eventData) => {
    var event = {};
    eventData.id && (event.id = eventData.id);
    eventData.EndTime && (event.end = (new Date(eventData.EndTime)).toString())
    eventData.StartTime && (event.start = (new Date(eventData.StartTime)).toString())
    eventData.Subject && (event.subject = eventData.Subject)
    eventData.Location && (event.location = eventData.Location)
    eventData.Description && (event.description = eventData.Description)
    eventData.Repeat && (event.isRecurring = eventData.Repeat)
    //eventData.repeatPatter && (event.repeatPatter = eventData.repeatPatter)
    /* eventData.id?event.id = eventData.id : null;
    eventData.end?event.end = (new Date(eventData.end)).toString() : null;
    eventData.start?event.start = (new Date(eventData.start)).toString():null;
    eventData.subject?event.subject = eventData.subject: null;
    eventData.location?event.location = eventData.location : null;
    eventData.description?event.description = eventData.description : null;
    eventData.isRecurring?event.isRecurring = eventData.isRecurring : null;
    eventData.repeatPatter?event.repeatPatter = eventData.repeatPatter: null;
    eventData.repeatCount?event.repeatCount = eventData.repeatCount : null; */
    return event;
  }

  //const addEventToServer = (eventData, scheduleObj) => {
  const addEventToServer = (eventData, currentSchedule) => {
    var eventObj = getEventObject(eventData);
    eventObj.id = currentSchedule.eventBase.getEventMaxID() + 1;
    var recPattern = document.querySelector('.e-recurrenceeditor').ej2_instances[0].value;
    if(recPattern) {
      eventObj.repeatPatter = recPattern
    }
    var eventBody = JSON.stringify(eventObj);
    fetch(domain, {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      },
      body: eventBody
    })
    .then((res) => {
      currentSchedule.addEvent(eventData);
      //alert("added event to server");
    })
    .catch((err) => {
      //alert("Error while adding event to server");
      console.log("Err: " + err);
    });
    dialogClose();
  }

  const updateEventToServer = (eventData, currentSchedule) => {
    var id = currentEvent.Id;
    var eventObj = getEventObject(eventData);
    eventObj.id = id;
    var recPattern = document.querySelector('.e-recurrenceeditor').ej2_instances[0].value;
    if(recPattern) {
      eventObj.repeatPatter = recPattern
    }
    var eventBody = JSON.stringify(eventObj);
    fetch(domain + '/?id='+id, {
      method: 'PUT',
       headers: {
         'Content-Type': 'application/json'
       },
      body: eventBody
    })
    .then((err, data) => {
      currentSchedule.saveEvent(eventData);
      //alert("Updated event to server");
    })
    .catch((err) => {
      //alert("Error while Updating event to server");
    });
  }

  //const deleteEventInServer = (eventData, scheduleObj) => {
  const deleteEventInServer = (eventData) => {
    var id = eventData.id;
    fetch(domain + '/id='+id, {
      method: 'DELETE',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      }
    })
    .then(() => {
      currentSchedule.deleteEvent(eventData);
      //alert("Deleted event in server");
    })
    .catch((err) => {
      //alert("Error while Deleting event to server")
    });
  }

  const addSyncfusionScheduler = () => {
    return (<div>
      <ButtonComponent id='add' title='Add'>Add</ButtonComponent>
      <ButtonComponent id='edit' title='Edit'>Edit</ButtonComponent>
      <ButtonComponent id='delete' title='Delete' >Delete</ButtonComponent>
      <ScheduleComponent currentView='Week' 
      ref={schedule => scheduleObj = schedule} 
      eventSettings={{dataSource: events}} 
      showQuickInfo={false} 
      popupOpen = {openPopUp.bind(this)} >
      <ViewsDirective>
        <ViewDirective option='Day' />
        <ViewDirective option='Week' />
        <ViewDirective option='Month' />
      </ViewsDirective>
      <Inject services={[Day, Week, Month, Agenda]} />
      </ScheduleComponent>
    </div>)
  }

  const openPopUp = (args) => {
    //alert("test");
    if (args.type === 'Editor' || args.type === 'QuickInfo') {
      currentSchedule = scheduleObj;
      currentEvent = args.type === 'QuickInfo'? scheduleObj.getEventDetails(args.target) : args.data;
      let dialogObj = (args.element).ej2_instances[0];
      let buttons;
      if (args.target.classList.contains('e-appointment')) {
        //currentEvent = scheduleObj.getEventDetails(args.target);
        buttons = [{
          buttonModel: { content: 'SAVE', isPrimary: true }, click: editEvent.bind(this)
        }, {
          buttonModel: { content: 'DELETE', cssClass: 'e-event-delete' }, click: eventDelete.bind(this)
        },
        {
          buttonModel: { content: 'CANCEL', cssClass: 'e-event-cancel' }, click: dialogClose.bind(this)
        }];
      } else {
        //currentEvent = scheduleObj.getEventDetails(args.target);
        buttons = [{
          buttonModel: { content: 'SAVE', isPrimary: true }, click: eventAdd.bind(this)
        }, {
          buttonModel: { content: 'CANCEL', cssClass: 'e-event-cancel' }, click: dialogClose.bind(this)
        }];
      }

      dialogObj.buttons = buttons;
      dialogObj.dataBind();
    }
  }

   const eventDelete = (e)=> {
    var tes = currentSchedule;
    const eventData = currentSchedule.activeEventData.event;
    deleteEventInServer(eventData, currentSchedule);
    dialogClose();
  }

  const eventAdd = (e) => {
    var tes = currentSchedule;
    const eventData = currentSchedule.eventWindow.getObjectFromFormData('e-schedule-dialog');
    addEventToServer(eventData, currentSchedule);
    dialogClose();
  }

  const editEvent = (e)=> {
    const eventData = currentSchedule.eventWindow.getObjectFromFormData('e-schedule-dialog');
    updateEventToServer(eventData, currentSchedule);
    dialogClose();
  }
  const dialogClose = ()=> {
    let dialogObj = (document.querySelector('.e-schedule-dialog')).ej2_instances[0];
    dialogObj.hide();
    //alert(" Event clsoed");
  }

  fetchEvents();
  return (
    <div className="App">
      {/* <div>{renderEntries()}</div> */}
      <div>{addSyncfusionScheduler()}</div>
    </div>
  );
}

export default App;
