    //             </div>
    //         </div>
    //     </div>
    // );
}

const handleAddUser = () => {
    var modalPopUp = document.getElementsByClassName("modal");
    modalPopUp.style.display = "block";
}

const AddWorkHours = () => {
    return (
        <div>
            <button onclick={() => {
                alert('add work hours');
            }}>
                Add Work hours
        </button>
        </div>
    );
}
