import React, { useState } from "react";
import "./App.css";
import Button from "@material-ui/core/Button";
import Select from "@material-ui/core/Select";
import { makeStyles } from "@material-ui/core/styles";
import InputLabel from "@material-ui/core/InputLabel";
import FormControl from "@material-ui/core/FormControl";
import TextField from "@material-ui/core/TextField";
import {
    KeyboardTimePicker
  } from '@material-ui/pickers';

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
}));

export const CalendarAdminBar = ({
  currentUser,
  setCurrentUser,
  userListItems,
}) => {
  const [userList, setUserList] = useState([]);
  return (
    <>
      <UserDropDown
        currentUser={currentUser}
        setCurrentUser={setCurrentUser}
        userList={userListItems}
      />
      <AddUser userList={userList} setUserList={setUserList} />
      <AddWorkHours />
    </>
  );
};

const UserDropDown = (currentUser, setCurrentUser, userListItems) => {
  const [user, setUsers] = useState([]);

  const classes = useStyles();

  return (
    <div className="App">
      <FormControl className={classes.formControl}>
        <InputLabel htmlFor="grouped-native-select">Current User</InputLabel>
        <Select native defaultValue="" id="grouped-native-select">
          <option aria-label="None" value="" />
          <optgroup label="Category 1">
            <option value={1}>Option 1</option>
            <option value={2}>Option 2</option>
          </optgroup>
          <optgroup label="Category 2">
            <option value={3}>Option 3</option>
            <option value={4}>Option 4</option>
          </optgroup>
        </Select>
      </FormControl>
    </div>
  );
};

const AddUser = () => {
  const classes = useStyles();
  return (
    <div>
      <FormControl className={classes.formControl}>
        <Button
          color="primary"
          variant="contained"
          style={{ justifyContent: "center" }}
          onClick={() => {
            var addUserPopUp = document.getElementsByClassName("modal")[0];
            addUserPopUp.style.display = "block";
          }}
        >
          Add user
        </Button>
      </FormControl>

      {/* <div id="myModal" class="modal"> */}
      <div id="addUserDialog" class="modal">
        <div class="modal-content">
          <div>
            <InputLabel htmlFor="grouped-native-select">
              User Details:
            </InputLabel>
            <FormControl className={classes.formControl}>
              <TextField id="standard-basic" key="name" label="Name" />
              <TextField id="standard-basic" key="email" label="Email" />
              <TextField id="standard-basic" key="phone" label="Phone" />
              <TextField id="standard-basic" key="location" label="Location" />
            </FormControl>
          </div>
          <Button
            color="primary"
            onClick={() => {
              var modalPopUp = document.getElementsByClassName("modal")[0];
              modalPopUp.style.display = "none";
            }}
          >
            Close
          </Button>
          <Button
            color="primary"
            onClick={() => {
              alert("save data");
            }}
          >
            Save
          </Button>
        </div>
      </div>
    </div>
  );

  // return (
  //     <div className="App">
  //         <button id="addUserBtn" onClick="handleAddUser()">
  //             Add User
  //         </button>
  //         <div id="myModal" class="modal">
  //             <div class="modal-content">
  //                 <p>Some text in the Modal..</p>
  //                 <button id="closeBut" onclick={() => {
  //                     var modalPopUp = document.getElementsByClassName("modal");
  //                     modalPopUp.style.display = "none";
  //                 }} />
  //                 <button id="saveBut" onclick={() => {
  //                     alert('save data');
  //                 }} />
  //             </div>
  //         </div>
  //     </div>
  // );
};

const handleAddUser = () => {
  var modalPopUp = document.getElementsByClassName("modal");
  modalPopUp.style.display = "block";
};

const AddWorkHours = () => {
    const classes = useStyles();
    const [startTime, setStartTime] = React.useState(new Date());
    const [endTime, setEndTime] = React.useState(new Date());

  return (
    <div>
      <FormControl className={classes.formControl}>
        <Button
          color="primary"
          variant="contained"
          style={{ justifyContent: "center" }}
          onClick={() => {
            var addWorkHoursPopup = document.getElementsByClassName("addWorkHours")[0];
            addWorkHoursPopup.style.display = "block";
          }}
        >
          Add Work hours
        </Button>
      </FormControl>
      <div id="addWorkHoursDialog" class="modal addWorkHours">
        <div class="modal-content">
          <div>
            <InputLabel htmlFor="grouped-native-select">
              User Work hours Details:
            </InputLabel>
            <FormControl className={classes.formControl}>
                <FormControl className={classes.formControl}>
                    <InputLabel htmlFor="grouped-native-select">Start Day</InputLabel>
                    <Select native defaultValue="" id="grouped-native-select">
                        <option value={1}>Sunday</option>
                        <option value={2}>Monday</option>
                        <option value={3}>Tuesday</option>
                        <option value={4}>Wednessday</option>
                        <option value={2}>Thursday</option>
                        <option value={3}>Friday</option>
                        <option value={4}>Saturday</option>
                    </Select>
                </FormControl>  
                <KeyboardTimePicker
                    margin="normal"
                    id="startTime-picker"
                    label="Time picker"
                    value={startTime}
                    onChange={(date) => {
                        setStartTime(date);
                    }}
                    KeyboardButtonProps={{
                        'aria-label': 'change time',
                    }}
                    />
                <FormControl className={classes.formControl}>
                    <InputLabel htmlFor="grouped-native-select">End Day</InputLabel>
                    <Select native defaultValue="" id="grouped-native-select">
                        <option value={1}>Sunday</option>
                        <option value={2}>Monday</option>
                        <option value={3}>Tuesday</option>
                        <option value={4}>Wednessday</option>
                        <option value={2}>Thursday</option>
                        <option value={3}>Friday</option>
                        <option value={4}>Saturday</option>
                    </Select>
                </FormControl>  
                <KeyboardTimePicker
                    margin="normal"
                    id="endTime-picker"
                    label="Time picker"
                    value={endTime}
                    onChange={(date) => {
                        setEndTime(date);
                    }}
                    KeyboardButtonProps={{
                        'aria-label': 'change time',
                    }}
                    />
            </FormControl>
          </div>
          <Button
            color="primary"
            onClick={() => {
              var modalPopUp = document.getElementsByClassName("addWorkHours")[0];
              modalPopUp.style.display = "none";
            }}
          >
            Close
          </Button>
          <Button
            color="primary"
            onClick={() => {
              alert("save data");
            }}
          >
            Save
          </Button>
        </div>
      </div>
    </div>
  );
};
