import React, {useState} from 'react';
import './App.css';
import {Inject, ScheduleComponent, Schedule, EventSettingsModel, Day, Week, Month, Agenda} from '@syncfusion/ej2-react-schedule';
import { ButtonComponent } from '@syncfusion/ej2-react-buttons';

function App() {

  const[entries, setEntries] = useState([]);
  const[calDataSource, setCalDataSource] = useState([]);

  // const calData = {EventSettingsModel : {
  //   dataSource : calDataSource
  // }}

  const getCalData = () => {
    return EventSettingsModel = {
      dataSource : calDataSource
    }
  }

  const fetchData = () => {
    fetch("http://localhost:3000/calendar")
    .then(response => response.json())
    .then(res => {
      console.log("Writing data");
      if(res && res.data){
        console.log("writing to entries");
        setEntries(res.data);
      }
    });
  }

  const fetchEvents = () => {
    fetch("http://localhost:3000/calendar/events")
    .then(response => response.json())
    .then(res => {
      if(res && res.events){
        setCalDataSource(res.events);
      }
    });
  }

  const loadTempEvents = () => {
    var coll = [];
    coll.push(
      { StartTime: new Date(2020, 4, 3, 8, 30),
        EndTime: new Date(2020, 4,3, 10, 45)
      });
      coll.push(
      { StartTime: new Date(2020, 4, 6, 8, 30),
        EndTime: new Date(2020, 4, 6, 10, 45)
      });
      coll.push(
        { StartTime: new Date(2020, 5, 3, 8, 30),
        EndTime: new Date(2020, 5, 3, 10, 45)
      });
      setCalDataSource(coll);
  }

  const renderEntries = () => {
    if(entries.length <= 0){
      return (<div>no entries available</div>);
    } else {
      return entries.map((val, key) => {
        return <div key={key}>Day: {val.day}, Available: {val.available}</div>
      });
    }
  }

  const addSyncfusionScheduler = () => {
    // calData : EventSettingsModel = {
    //   dataSource : calDataSource
    // }
    //alert(calDataSource);
    //loadTempEvents();
    var coll = [];
    coll.push(
      { StartTime: new Date(2020, 4, 3, 8, 30),
        EndTime: new Date(2020, 4,3, 10, 45)
      });
      coll.push(
      { StartTime: new Date(2020, 4, 6, 8, 30),
        EndTime: new Date(2020, 4, 6, 10, 45)
      });
      coll.push(
        { StartTime: new Date(2020, 5, 3, 8, 30),
        EndTime: new Date(2020, 5, 3, 10, 45)
      });

      //Check eventlisterners for Add, delete, edit
      let scheduleObj = new Schedule({
        height: '550px',
        selectedDate: new Date(2018, 1, 15),
        views: ['Day', 'Week', 'WorkWeek', 'Month'],
        eventSettings: {
            dataSource: scheduleData
        }
    });
    scheduleObj.appendTo('#Schedule');

      //setCalDataSource(coll);

    //return <ScheduleComponent currentView='Week' eventSettings={{dataSource: coll}}>
    // return <ScheduleComponent currentView='Week' eventSettings={{dataSource: calDataSource}}>
    //   <Inject services={[Day, Week, Month, Agenda]} />
    // </ScheduleComponent>
      return (<div>
        <ButtonComponent id='add' title='Add'>Add</ButtonComponent>
        <ButtonComponent id='edit' title='Edit'>Edit</ButtonComponent>
        <ButtonComponent id='delete' title='Delete' >Delete</ButtonComponent>
        <ScheduleComponent currentView='Week' eventSettings={{dataSource: calDataSource}} 
          >
        <Inject services={[Day, Week, Month, Agenda]} />
        </ScheduleComponent>
      </div>)

  }

  //fetchData();
  fetchEvents();

  return (
    <div className="App">
      {/* <div>{renderEntries()}</div> */}

      <div>{addSyncfusionScheduler()}</div>

    </div>
  );
}

export default App;
