import React, {useState} from 'react';
import logo from './logo.svg';
import './App.css';

function App() {

  const[entries, setEntries] = useState([]);

  const fetchData = () => {
    fetch("http://localhost:3000/calendar")
    .then(response => response.json())
    .then(res => {
      if(res && res.data){
        console.log("writing to entries");
        this.setCourses(res.data);
      }
    });
  }

  const renderCourses = () =>{
    if(courses.length <= 0){
      return(
        <div>No data to load</div>
      );
    } else {
        return courses.map(val => {
          return <div>{val.name} ------ {val.category}</div>
        });
    }
  }

  const renderEntries = () => {
    if(entries.length <= 0){
      return (<div>no entries available</div>);
    } else {
      return this.state.entries.map((val, key) => {
        return <div key={key}>Day: {val.day}, Available: {val.available}</div>
      });
    }
  }

  fetchData();
  return (
    <div className="App">
      <div>{this.renderEntries()}</div>
    </div>
  );
}

export default App;
