import React, { useState, useRef } from "react";
import "./App.css";
import Button from "@material-ui/core/Button";
import Select from "@material-ui/core/Select";
import { makeStyles } from "@material-ui/core/styles";
import InputLabel from "@material-ui/core/InputLabel";
import FormControl from "@material-ui/core/FormControl";
import TextField from "@material-ui/core/TextField";
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
}));

/* export const CalendarAdminBar = ({
  currentUser,
  setCurrentUser,
  userListItems,
}) => {
  const [userList, setUserList] = useState([]);
  return (
    <>
      <UserDropDown
        currentUser={currentUser}
        setCurrentUser={setCurrentUser}
        userList={userListItems}
      />
      <AddUser userList={userList} setUserList={setUserList} />
      <AddWorkHours />
    </>
  );
}; */

export const CalendarAdminBar = ({ currentUser, setCurrentUser, usersList, setUsersList}) => {
    return (
      <>
        <UserDropDown
          currentUser={currentUser}
          setCurrentUser={setCurrentUser}
          usersList={usersList}
        />
        <AddUser usersList={usersList} setUsersList={setUsersList} />
        <AddWorkHours />
      </>
    );
  };

const UserDropDown = ({ currentUser, setCurrentUser, usersList}) => {
  const [user, setUsers] = useState([]);

  const classes = useStyles();

  const handleSelectionChange = (e) => {
    setCurrentUser(e.target.value);
    var x =10;
    var y = x;
  }

  return (
    <div className="App">
        <FormControl className={classes.formControl}>
        <InputLabel htmlFor="grouped-native-select">Current User</InputLabel>
        <Select native defaultValue="" id="grouped-native-select"
        onChange={handleSelectionChange}>
            {usersList.map((user) => <option key={user.id} value={user.id}>{user.name}</option>)}
        </Select>
      </FormControl>
      {/* <FormControl className={classes.formControl}>
        <InputLabel htmlFor="grouped-native-select">Current User</InputLabel>
        <Select native defaultValue="" id="grouped-native-select">
          <option aria-label="None" value="" />
          <optgroup label="Category 1">
            <option value={1}>Option 1</option>
            <option value={2}>Option 2</option>
          </optgroup>
          <optgroup label="Category 2">
            <option value={3}>Option 3</option>
            <option value={4}>Option 4</option>
          </optgroup>
        </Select>
      </FormControl> */}
    </div>
  );
};

const AddUser = ({usersList, setUsersList}) => {
  const name = useRef("");
  const email = useRef("");
  const phone = useRef("");
  const location = useRef("");
  const classes = useStyles();

  const addNewUser = (userObj) => {
    //var eventObj = getEventObject(eventData);
      var userBody = JSON.stringify(userObj);
      fetch('http://localhost:3000/users', {
        method: 'POST',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json',
        },
        body: userBody
      })
      .then((res) => {
        userObj.id = res.status;
        setUsersList([...usersList, userObj]);
      })
      .catch((err) => {
        console.log("Err: " + err);
      });
  }

  const closeDialog = (className) => {
    var popUp = document.getElementsByClassName(className)[0];
    popUp.style.display = "none";
    name.current.value = '';
    email.current.value = '';
    phone.current.value = '';
    location.current.value = '';
  }

  return (
    <div>
      <FormControl className={classes.formControl}>
        <Button
          color="primary"
          variant="contained"
          style={{ justifyContent: "center" }}
          onClick={() => {

            var addUserPopUp = document.getElementsByClassName("modal")[0];
            addUserPopUp.style.display = "block";
          }}
        >
          Add user
        </Button>
      </FormControl>

      {/* <div id="myModal" class="modal"> */}
      <div id="addUserDialog" class="modal addUser">
        <div class="modal-content">
          <div>
            <InputLabel htmlFor="grouped-native-select">
              User Details:
            </InputLabel>
            <FormControl className={classes.formControl}>
              <TextField  key="name" label="Name" inputRef={name} />
              <TextField  key="email" label="Email" inputRef={email}/>
              <TextField  key="phone" label="Phone" inputRef={phone}/>
              <TextField  key="location" label="Location" inputRef={location}/>
            </FormControl>
          </div>
          <Button
            color="primary"
            onClick={() => {
              // var modalPopUp = document.getElementsByClassName("modal")[0];
              // modalPopUp.style.display = "none";
              // var addUserPopUp = document.getElementsByClassName("addUser")[0];
              // addUserPopUp.style.display = "none";
              closeDialog("addUser");
            }}
          >
            Close
          </Button>
          <Button
            color="primary"
            onClick={(e) => {
              e.preventDefault();
              //alert("save data");
              //var addUserPopUp = document.getElementsByClassName("addUser")[0];
              var userObj = {};
              userObj.name = name.current.value;
              userObj.email = email.current.value;
              userObj.phone = phone.current.value;
              userObj.location = location.current.value;
              addNewUser(userObj);
              closeDialog("addUser");
              //setUsersList([usersList..., {items}])
            }}
          >
            Save
          </Button>
        </div>
      </div>
    </div>
  );

  // return (
  //     <div className="App">
