import React, {useState} from 'react';
import './App.css';
import {Inject, ScheduleComponent, ViewsDirective, ViewDirective, Day, Week, Month, Agenda, Resize, DragAndDrop} from '@syncfusion/ej2-react-schedule';
import { ButtonComponent } from '@syncfusion/ej2-react-buttons';

function App() {

  const[entries, setEntries] = useState([]);
  const[calDataSource, setCalDataSource] = useState([]);

  // const calData = {EventSettingsModel : {
  //   dataSource : calDataSource
  // }}

  // const getCalData = () => {
  //   return EventSettingsModel = {
  //     dataSource : calDataSource
  //   }
  // }

  const scheduleObj = {}

  const fetchData = () => {
    fetch("http://localhost:3000/calendar")
    .then(response => response.json())
    .then(res => {
      console.log("Writing data");
      if(res && res.data){
        console.log("writing to entries");
        setEntries(res.data);
      }
    });
  }

  const fetchEvents = () => {
    fetch("http://localhost:3000/calendar/events")
    .then(response => response.json())
    .then(res => {
      if(res && res.events){
        setCalDataSource(res.events);
      }
    });
  }

  const loadTempEvents = () => {
    var coll = [];
    coll.push(
      { StartTime: new Date(2020, 4, 3, 8, 30),
        EndTime: new Date(2020, 4,3, 10, 45)
      });
      coll.push(
      { StartTime: new Date(2020, 4, 6, 8, 30),
        EndTime: new Date(2020, 4, 6, 10, 45)
      });
      coll.push(
        { StartTime: new Date(2020, 5, 3, 8, 30),
        EndTime: new Date(2020, 5, 3, 10, 45)
      });
      setCalDataSource(coll);
  }

  const renderEntries = () => {
    if(entries.length <= 0){
      return (<div>no entries available</div>);
    } else {
      return entries.map((val, key) => {
        return <div key={key}>Day: {val.day}, Available: {val.available}</div>
      });
    }
  }

  const addSyncfusionScheduler = () => {
    // calData : EventSettingsModel = {
    //   dataSource : calDataSource
    // }
    //alert(calDataSource);
    //loadTempEvents();
    var coll = [];
    coll.push(
      { StartTime: new Date(2020, 4, 3, 8, 30),
        EndTime: new Date(2020, 4,3, 10, 45)
      });
      coll.push(
      { StartTime: new Date(2020, 4, 6, 8, 30),
        EndTime: new Date(2020, 4, 6, 10, 45)
      });
      coll.push(
        { StartTime: new Date(2020, 5, 3, 8, 30),
        EndTime: new Date(2020, 5, 3, 10, 45)
      });

    //Old code
    /*return (<div>
      <ButtonComponent id='add' title='Add'>Add</ButtonComponent>
      <ButtonComponent id='edit' title='Edit'>Edit</ButtonComponent>
      <ButtonComponent id='delete' title='Delete' >Delete</ButtonComponent>
      <ScheduleComponent currentView='Week' eventSettings={{dataSource: calDataSource}} >
      <Inject services={[Day, Week, Month, Agenda]} />
      </ScheduleComponent>
    </div>)*/

    return (<div>
      <ButtonComponent id='add' title='Add'>Add</ButtonComponent>
      <ButtonComponent id='edit' title='Edit'>Edit</ButtonComponent>
      <ButtonComponent id='delete' title='Delete' >Delete</ButtonComponent>
      <ScheduleComponent currentView='Week' 
      ref={schedule => scheduleObj = schedule} 
      eventSettings={{dataSource: coll}} 
      popupOpen = {openPopUp.bind(this)} >
      <ViewsDirective>
        <ViewDirective option='Day' />
        <ViewDirective option='Week' />
        <ViewDirective option='Month' />
      </ViewsDirective>
      <Inject services={[Day, Week, Month, Agenda]} />
      </ScheduleComponent>
    </div>)

  }

  const openPopUp = (args) => {
    //alert("test");
    if (args.type === 'Editor') {
      let dialogObj = (args.element).ej2_instances[0];
      let buttons;
      if (args.target.classList.contains('e-appointment')) {
        //this.currentEvent = this.scheduleObj.getEventDetails(args.target);
        //this.currentEvent = scheduleObj.getEventDetails(args.target);
        buttons = [{
          buttonModel: { content: 'SAVE', isPrimary: true }, click: editEvent.bind(this)
        }, {
          buttonModel: { content: 'DELETE', cssClass: 'e-event-delete' }, click: eventDelete.bind(this)
        },
        {
          buttonModel: { content: 'CANCEL', cssClass: 'e-event-cancel' }, click: dialogClose.bind(this)
        }];
      } else {
        buttons = [{
          buttonModel: { content: 'SAVE', isPrimary: true }, click: eventAdd.bind(this)
        }, {
          buttonModel: { content: 'CANCEL', cssClass: 'e-event-cancel' }, click: dialogClose.bind(this)
        }];
      }
      dialogObj.buttons = buttons;
      dialogObj.dataBind();
    }
  }

  const eventAdd = (e) => {
    // const data = this.scheduleObj.getCellDetails(this.scheduleObj.getSelectedElements());
    // const eventData = this.scheduleObj.eventWindow.getObjectFromFormData('e-schedule-dialog');
    // eventData.Id = this.scheduleObj.eventBase.getEventMaxID() + 1;
    // this.scheduleObj.addEvent(eventData);
    // this.dialogClose();
    alert("add Event called");
  }
  const eventDelete = (e)=> {
    // const eventData = this.scheduleObj.activeEventData.event;
    // this.scheduleObj.deleteEvent(eventData);
    // this.dialogClose();
    alert("delete Event called");
  }

  const editEvent = (e)=> {
    // const eventData = this.scheduleObj.eventWindow.getObjectFromFormData('e-schedule-dialog');
    // eventData.Id = this.currentEvent.Id;
    // this.scheduleObj.saveEvent(eventData);
    // this.dialogClose();
    alert("edit Event called");
  }
  const dialogClose = ()=> {
    // let dialogObj = (document.querySelector('.e-schedule-dialog')).ej2_instances[0];
    // dialogObj.hide();
    alert(" Event clsoed");
  }

  //fetchData();
  //fetchEvents();
  //loadTempEvents();

  return (
    <div className="App">
      {/* <div>{renderEntries()}</div> */}
      <div>{addSyncfusionScheduler()}</div>
    </div>
  );
}

export default App;
