import React, { useState } from "react";
import "./App.css";
import Button from "@material-ui/core/Button";
import Select from "@material-ui/core/Select";
import { makeStyles } from "@material-ui/core/styles";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import ListSubheader from "@material-ui/core/ListSubheader";
import FormControl from "@material-ui/core/FormControl";

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
}));

export const CalendarAdminBar = ({ currentUser, setCurrentUser, userListItems}) => {
  const [userList, setUserList] = useState([]);
  return (
    <>
      <UserDropDown
        currentUser={currentUser}
        setCurrentUser={setCurrentUser}
        userList={userListItems}
      />
      <AddUser userList={userList} setUserList={setUserList} />
      <AddWorkHours />
    </>
  );
};

const UserDropDown = (currentUser, setCurrentUser, userListItems) => {
  const [user, setUsers] = useState([]);

    const classes = useStyles();

  return (
    <div className="App">

      <FormControl className={classes.formControl}>
        <InputLabel htmlFor="grouped-native-select">Current User</InputLabel>
        <Select native defaultValue="" id="grouped-native-select">
          <option aria-label="None" value="" />
          <optgroup label="Category 1">
            <option value={1}>Option 1</option>
            <option value={2}>Option 2</option>
          </optgroup>
          <optgroup label="Category 2">
            <option value={3}>Option 3</option>
            <option value={4}>Option 4</option>
          </optgroup>
        </Select>
      </FormControl>
    </div>
  );
};

const AddUser = () => {
    const classes = useStyles();
  return (
    <div>
        <FormControl className={classes.formControl}>
            <Button
            color="primary" variant="contained" style={{justifyContent: 'center'}}
            onClick={() => {
            var modalPopUp = document.getElementsByClassName("modal")[0];
            modalPopUp.style.display = "block";
            }}>
                Add user
            </Button>
        </FormControl>

      <div id="myModal" class="modal">
        <div class="modal-content">
          <p>Some text in the Modal..</p>
          <Button
            color="primary"
            onClick={() => {
              var modalPopUp = document.getElementsByClassName("modal")[0];
              modalPopUp.style.display = "none";
            }}
            >
            Close
          </Button>
          <Button
            color="primary"
            onClick={() => {
              alert("save data");
            }}
          >
            Save
          </Button>
        </div>
      </div>
    </div>
  );

