import React, { Component } from 'react';
import './App.css';

class App extends Component {

  constructor(props){
    super(props);
    this.state = {
      entries: []
    }
  }

  getData(){
    fetch("http://localhost:3000/calendar")
    .then(response => response.json())
    .then(res => {
      if(res && res.data){
        console.log("writing to entries");
        this.setState({entries: [...this.state.users, ...res.data]})
      }
    });
  }

  renderEntries(){
    if(this.state.users.length <= 0){
      return <div>no entries available</div>
    } else {
      return this.state.users.map((val, key) => {
        return <div key={key}>Day: {val.day}, Available: {val.available}</div>
      });
    }
  }

  render() {
      return (
      <div className="App">
        {this.renderEntries()}
      </div>
      );
  }
}

export default App;
