import React, {useState} from 'react';
import './App.css';
import {Inject, ScheduleComponent, Day, Week, Month, Agenda} from '@syncfusion/ej2-react-schedule';

function App() {

  const[entries, setEntries] = useState([]);

  const fetchData = () => {
    fetch("http://localhost:3000/calendar")
    .then(response => response.json())
    .then(res => {
      console.log("Writing data");
      if(res && res.data){
        console.log("writing to entries");
        setEntries(res.data);
      }
    });
  }

  const renderEntries = () => {
    if(entries.length <= 0){
      return (<div>no entries available</div>);
    } else {
      return entries.map((val, key) => {
        return <div key={key}>Day: {val.day}, Available: {val.available}</div>
      });
    }
  }

  const addSyncfusionScheduler = () => {
    return <ScheduleComponent currentView='Month'>
      <Inject services={[Day, Week, Month, Agenda]} />
    </ScheduleComponent>
  }

  fetchData();
  return (
    <div className="App">
      {/* <div>{renderEntries()}</div> */}

      <div>{addSyncfusionScheduler()}</div>

    </div>
  );
}

export default App;
