import React, { useState } from "react";
import "./App.css";
import Button from "@material-ui/core/Button";
import Select from "@material-ui/core/Select";
import { makeStyles } from "@material-ui/core/styles";
import InputLabel from "@material-ui/core/InputLabel";
import FormControl from "@material-ui/core/FormControl";
import TextField from "@material-ui/core/TextField";

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
}));

/* export const CalendarAdminBar = ({
  currentUser,
  setCurrentUser,
  userListItems,
}) => {
  const [userList, setUserList] = useState([]);
  return (
    <>
      <UserDropDown
        currentUser={currentUser}
        setCurrentUser={setCurrentUser}
        userList={userListItems}
      />
      <AddUser userList={userList} setUserList={setUserList} />
      <AddWorkHours />
    </>
  );
}; */

export const CalendarAdminBar = ({ currentUser, setCurrentUser, usersList, setUsersList}) => {
    return (
      <>
        <UserDropDown
          currentUser={currentUser}
          setCurrentUser={setCurrentUser}
          usersList={usersList}
        />
        <AddUser usersList={usersList} setUsersList={setUsersList} />
        <AddWorkHours />
      </>
    );
  };

const UserDropDown = ({ currentUser, setCurrentUser, usersList}) => {
  const [user, setUsers] = useState([]);

  const classes = useStyles();

  /* const items = [];
  items.push({id:1, value: 'one'});
  items.push({id:2, value: 'two'});
  items.push({id:3, value: 'three'});
  items.push({id:4, value: 'four'});
  items.push({id:5, value: 'five'});
  items.push({id:6, value: 'six'}); */

  return (
    <div className="App">
        <FormControl className={classes.formControl}>
        <InputLabel htmlFor="grouped-native-select">Current User</InputLabel>
        <Select native defaultValue="" id="grouped-native-select">
            {usersList.map((user) => <option key={user.id} value={user.id}>{user.name}</option>)}
        </Select>
      </FormControl>
      {/* <FormControl className={classes.formControl}>
        <InputLabel htmlFor="grouped-native-select">Current User</InputLabel>
        <Select native defaultValue="" id="grouped-native-select">
          <option aria-label="None" value="" />
          <optgroup label="Category 1">
            <option value={1}>Option 1</option>
            <option value={2}>Option 2</option>
          </optgroup>
          <optgroup label="Category 2">
            <option value={3}>Option 3</option>
            <option value={4}>Option 4</option>
          </optgroup>
        </Select>
      </FormControl> */}
    </div>
  );
};

const AddUser = ({usersList, setUsersList}) => {
  const name = useRef("");
  const email = useRef("");
  const phone = useRef("");
  const location = useRef("");
  const classes = useStyles();
  return (
    <div>
      <FormControl className={classes.formControl}>
        <Button
          color="primary"
          variant="contained"
          style={{ justifyContent: "center" }}
          onClick={() => {
            var addUserPopUp = document.getElementsByClassName("modal")[0];
            addUserPopUp.style.display = "block";
          }}
        >
          Add user
        </Button>
      </FormControl>

      {/* <div id="myModal" class="modal"> */}
      <div id="addUserDialog" class="modal addUser">
        <div class="modal-content">
          <div>
            <InputLabel htmlFor="grouped-native-select">
              User Details:
            </InputLabel>
            <FormControl className={classes.formControl}>
              <TextField  key="name" label="Name" ref={name} />
              <TextField  key="email" label="Email" ref={email}/>
              <TextField  key="phone" label="Phone" ref={phone}/>
              <TextField  key="location" label="Location" ref={location}/>
            </FormControl>
          </div>
          <Button
            color="primary"
            onClick={() => {
              // var modalPopUp = document.getElementsByClassName("modal")[0];
              // modalPopUp.style.display = "none";
              var addUserPopUp = document.getElementsByClassName("addUser")[0];
              addUserPopUp.style.display = "none";
            }}
          >
            Close
          </Button>
          <Button
            color="primary"
            onClick={() => {
              alert("save data");
              var addUserPopUp = document.getElementsByClassName("addUser")[0];
              var x = 10;
              var y = x;
              //setUsersList([usersList..., {items}])
            }}
          >
            Save
          </Button>
        </div>
      </div>
    </div>
  );

  // return (
  //     <div className="App">
  //         <button id="addUserBtn" onClick="handleAddUser()">
  //             Add User
  //         </button>
  //         <div id="myModal" class="modal">
  //             <div class="modal-content">
  //                 <p>Some text in the Modal..</p>
  //                 <button id="closeBut" onclick={() => {
  //                     var modalPopUp = document.getElementsByClassName("modal");
  //                     modalPopUp.style.display = "none";
  //                 }} />
  //                 <button id="saveBut" onclick={() => {
  //                     alert('save data');
  //                 }} />
  //             </div>
  //         </div>
  //     </div>
  // );
};

const handleAddUser = () => {
  var modalPopUp = document.getElementsByClassName("modal");
  modalPopUp.style.display = "block";
};

const AddWorkHours = () => {
    const classes = useStyles();
    const [startTime, setStartTime] = React.useState(new Date());
    const [endTime, setEndTime] = React.useState(new Date());

  return (
    <div>
      <FormControl className={classes.formControl}>
        <Button
          color="primary"
          variant="contained"
          style={{ justifyContent: "center" }}
          onClick={() => {
            var addWorkHoursPopup = document.getElementsByClassName("addWorkHours")[0];
            addWorkHoursPopup.style.display = "block";
          }}
        >
          Add Work hours
        </Button>
      </FormControl>
      <div id="addWorkHoursDialog" class="modal addWorkHours">
        <div class="modal-content">
          <div>
            <InputLabel htmlFor="grouped-native-select">
              User Work hours Details:
            </InputLabel>
            <FormControl className={classes.formControl}>
                <FormControl className={classes.formControl}>
                    <InputLabel htmlFor="grouped-native-select">Start Day</InputLabel>
                    <Select native defaultValue="" id="grouped-native-select">
                        <option value={1}>Sunday</option>
                        <option value={2}>Monday</option>
                        <option value={3}>Tuesday</option>
                        <option value={4}>Wednessday</option>
                        <option value={2}>Thursday</option>
                        <option value={3}>Friday</option>
                        <option value={4}>Saturday</option>
                    </Select>
                </FormControl>  
                <form class="clock-textField clock-container" noValidate>
                    <TextField
                        id="endTime"
                        label="End time"
                        type="time"
                        defaultValue="05:00"
